# Storage provider used to write reference files firectly from the bridge
class StorageProvider:
    # File handle
    class _FileHandle:
        def __init__(self, path, mode):
            self.path = path
            self.mode = mode

            # Data is first stored into a temporary file path, then copied to the destination when finished.
            if mode == "a":
                # Clear before appending
                open(path, "w").close()
            
            self.handle = open(path, mode)

        # Must read from the file handle
        def read(self, amount_bytes):
            return self.handle.read(amount_bytes)

        # Must append data to the passed file handle
        def write(self, data):
            self.handle.write(data)

        # Must close the file handle
        def close(self):
            self.handle.close()
            self.handle = None


    def size(self, path):
        return stat(path).st_size

    # Must return an opened file handle. See StorageFileHandle class.
    def open(self, path, mode):
        return self._FileHandle(
            path = path,
            mode = mode
        )



